$(document).ready(function () {
  $(".qomticket")
    .first()
    .on("ready.flickity", function () {
      $(".qomticket").removeClass("loading")
      setTimeout(() => {
        $(".qomticket").css("visibility", "visible")
      }, 300)
    }),
    $(window).width() >= 600
      ? $(".qomticket").flickity({
          rightToLeft: !0,
          freeScroll: !0,
          contain: !0,
          pageDots: !1,
          freeScrollFriction: 0.2,
          cellAlign: "right",
          imagesLoaded: !0,
          setGallerySize: false
        })
      : ($(".qomticket").addClass("toScroll"),
        $(".qomticket .qomSingle").addClass("toScroller"),
        $(".qomticket").removeClass("loading")),
    $(".qomticket").css("visibility", "visible")
})
