$(document).ready(function () {
  if ($(window).width() >= 600) {
    $(".qomticket")
      .first()
      .on("ready.flickity", function () {
        console.log("yoyo")
        $(".qomticket").removeClass("loading")
      })

    $(".qomticket").flickity({
      rightToLeft: true,
      freeScroll: true,
      contain: true,
      pageDots: false,
      freeScrollFriction: 0.2,
      cellAlign: "right",
      imagesLoaded: true,
      setGallerySize: false
    })
  } else {
    $(".qomticket").addClass("toScroll")
    $(".qomticket .qomSingle").addClass("toScroller")
    $(".qomticket").removeClass("loading")
  }
})
